.create__preview {
	flex-basis: 40%;
}

.create__paper {
	background: #fff;
	text-align: center;
	width: 100%;
	padding: 12px;
	border-radius: 4px;
}

.create__wrapper {
	display: flex;
	margin-top: 40px;
}

.paper__title, 
.paper__desc, 
.paper__pics {
	margin: 10px 0;
	font-size: 14px;
	color: #afafaf;
}


.paper__pics {
	margin-left: auto;
	margin-right: auto;
	padding-top: 20px;
	width: 90%;
	border-top: 1px solid rgba(40,44,45,0.2);
}

.title__input {
	border: none;
	border: solid 1px #d7d7d7;
	border-radius: 10px;
	padding: 10px;
	width: 90%;
	text-align: center;
	font-size: 20px;
	margin-bottom: 40px;
}

.title__input:focus, .desc__input:focus {
	outline: none;
}

.desc__input {
	resize: none;
	width: 90%;
	border: none;
	border: solid 1px #d7d7d7;
	border-radius: 10px;
	padding: 10px;
	font-size: 20px;
	margin-bottom: 40px;
}

.paper__grid {
	display: -ms-grid;
	display: grid;
	-ms-grid-columns: (minmax(100px, 1fr))[auto-fill];
	    grid-template-columns: repeat(auto-fill, minmax(100px, 1fr));
	grid-row-gap: 10px;
}

.placeholder {
	background-size: 50%;
}

.add-icon {
	position: relative;
	top: -112px;
	left: 80px;
}
